// ==============================
// документация на слайдер
// https://github.com/akiran/react-slick
//
// ==============================

import React, { Component } from "react";
import Slider from "./slick/slider"
import './slick/slick.css'
import { Container, Row, Col } from 'reactstrap';

export default class VerticalStringSlider extends Component {
  render() {
    const settings = {
      autoplay: true,
      dots: false,
      arrows: false,
      infinite: true,
      slidesToShow: 1,
      slidesToScroll: 1,
      vertical: true,
      verticalSwiping: true,
      speed: 500,
      autoplaySpeed: 2000,
      beforeChange: function (currentSlide, nextSlide) {
        console.log("before change", currentSlide, nextSlide);
      },
      afterChange: function (currentSlide) {
        console.log("after change", currentSlide);
      }
    };
    return (
      <div className="hero-area">
        <Container>
          <Row className="items-center" sm="12" lg="4">
            {/* <!-- Breaking News Widget --> */}
            <div className="breaking-news-area d-flex align-items-center">
              <div class="news-title">
                <p>Breaking News</p>
              </div>
              <Slider {...settings} className="ticker">
                <div>
                  <h3>111111111111111111111 11111111111111
                     111111111111111111 11111111111 11111111 11111111</h3>
                </div>    
                <div>
                  <h3>2</h3>
                </div>
                <div>
                  <h3>3</h3>
                </div>
                <div>
                  <h3>4</h3>
                </div>
                <div>
                  <h3>5</h3>
                </div>
                <div>
                  <h3>6</h3>
                </div>
              </Slider>
            </div>
          </Row>
        </Container>
      </div>

    );
  }
}
